INSERT INTO `books` (`bid`, `title`, `summary`, `yayinevi`, `shop`, `aid`, `year`, `img`, `language`, `price`, `gid`) VALUES
(1, 'Yitirişler', 'Siz kimsesiz bir yenilgisiniz benim gibi', 'KİTABEVİ', NULL, 1, '1998-12-31', 'full/082d7891f11d2a872bca4d31c03eb4de97fcc155.jpg', 'TÜRKÇE', ' 5,00', 1),
(2, 'Din ve Cemiyet', 'Bir müslüman nelere inanır, neler yapar? İslamiyet insanlara nasıl tesir eder? Müslüman ferd ve müslüman cemiyet nasıl olmalıdır? İslam, bütün zamanların ferdleri ve cemiyetleri için, onları dünyada mutlu edecek esasları kendinde toplamıştır.', 'KİTABEVİ', NULL, 2, '1998-12-31', 'full/7fb08c02d194e3562bb2ce06a8858f16bc49f96f.jpg', 'TÜRKÇE', ' 15,00', 2),
(3, 'Osmanlı Eyaletinden Üçüncü Bulgar Çarlığına', 'Özellikle son ikiyüz yıl içersinde, Osmanlı idaresi altında yaşayan Hıristiyan unsurlar birçok güç tarafından kullanılmıştır. "Himaye siyaseti"de denilen bu uygulama ile Rusya Ortodokslar, Fransa katolikler, İngiltere de Protestanlar üzerinde politikalar ürettiler. Bu durum, Osmanlı devletinin iç politikada da serbest kararlar alabilmesini engellemiştir. Osmanlı Devletinin politikası, Avrupa devletlerine ait çıkarların gözetildiği ve hassas dengelerin hesaba katıldığı ince bir çizgi üzerinde yürütüldü. İçerde alınan birçok karar, dış müdahaleler yüzünden başarıyla uygulanamadı. Çünkü Osmanlı topraklarının coğrafi konumu, bir Japonya gibi gözlerden uzak değildi. Bu durumdan, gayrimüslimlerle birlikte Müslüman ahali de zarar gördü. Devletin her bölgesi ayrı bir probleme sahne oldu.', 'KİTABEVİ', NULL, 3, '1996-12-31', 'full/b61798aea7542ed42dc6883c739e2090bf2733df.jpg', 'TÜRKÇE', ' 15,00', 3),
(4, '''Brifing''deki İrtica Türkiye''de Militarizm Yönetim İlişkileri ve İslam', '"Türkiye''de "din" korku unsuru olmaktan çıkarılmalıdır. Din kontrol edilerek irticaın önlenemeyeceği artık anlaşılmalıdır. İslam üzerindeki yönlendirme ve denetim yerine serbesti yolu seçilmelidir. İslam geçmişte olduğu gibi çoğulcu, kavrayıcı yaklaşımlarıyla toplumumuzun sağaltılmasıyla ve gelişmesi yönünden rol oynamaya devam etmelidir. İşte o zaman "irtica" ortadan savuşur."', 'KİTABEVİ', NULL, 4, '1998-12-31', 'full/4851aa50b7f937fc1168dbb4b3a111039b2ea211.jpg', 'TÜRKÇE', ' 6,00', 4),
(5, 'Türkiye''de Tercüme Müesseseleri', 'Bu çalışma Lale devrinden, Hasan Ali Yücel dönemine kadar devlet tarafından tesis edilen tercüme heyetleri, müesseseleri, encümenleri üzerinde durmaktadır. Efkar-ı umumiyenin görüş ufkunu Doğudan ve Batıdan eserler tercüme ederek genişletmek maksadıyla devletin ciddi bir şekilde ele aldığı bu faaliyet başarılı olmuş mudur? Tercüme hareketi hangi alanlarda yoğunlaşmıştır? Tesis edilen müesseselerde kimler görev almıştır? Tercüme edilen eserler nelerdir? Devletin bu konudaki politikaları hangi düşünceye göre oluşturulmuştur? Elinizdeki kitap bütün bu soruları tartışmaya açmakta ve cevaplamaya çalışmaktadır. ', 'KİTABEVİ', NULL, 5, '1998-06-01', 'full/485e55678d339aa5f0f51bba0e1405b8c4009245.jpg', 'TÜRKÇE', ' 20,00', 5),
(6, 'Hürriyet Ve İtilaf Fırkası Nasıl Doğdu Nasıl Öldü', 'Fırka müşkilata düştüğü, perişan olmaya başladığı, fırka mensublarını tehlike kavradığı anda Ferid Paşa fırkayı da, arkadaşlarını da terkedip, fırkadan istifa etmişti. Ferid Paşa, iyi gün dostu imiş. Yine anlaşıldı ki bu zat, kuvvete itibar ediyor. Kuvvet gidince o da adamı bırakıyormuş; felakette ortak olmak gibi bir mertliği yokmuş. Demek onun dostluktan maksadı, dostlarını, bir şey elde etmek için alet etmekten ibaretmiş! İşte bu işlerde, Ferid Paşa, ahlakının zayıf kısımlarından bir takımını meydana çıkarmıştı.', 'KİTABEVİ', NULL, 6, '1996-12-31', 'full/844255d8492c9b9338f6107f992c12bbc0160a53.jpg', 'TÜRKÇE', ' 10,00', 6),
(7, 'Sultan II. Abdülhamid''in Hal Tercümesi', 'Sultan II. Abdülhamid döneminde yurtdışına kaçanların yaptıkları yayınların tamamına yakını, Padişah şahsını hedef alan, aleyhte yayınlardır. Bunların büyük bir kısmı muhalefetlerinde ciddi ve kararlı ise de, diğer kısmının amacı, kısa yoldan para kazanmaktır. Çünkü II. Abdülhamid, bu muhalif yayınları siyasi nüfuzunu kullanarak ya da satın alarak kapatmaya çalışmıştır. Bu yayın sahipleri, münasebet düştükçe, Padişah''ın sadık bir "bende"si olarak, Babıali''nin herhangi bir dairesinde, memur olarak görevlendirilmişlerdir. Amansız birer muhalif gibi görünen bu "göstermelik" gazete sahipleri, "Padişahım Çok Yaşa!" diye tezahüratta bulunan gruba dahil olmuşlardır. Elinizdeki bu eser, "Padişahım Çok Yaşa!" grubuna bir örnektir. Başlangıçta "Hilafet" gazetesi ile Sultan''a muhalif yayın yapmasına rağmen, daha sonra bundan vazgeçen ve bu değişiklikten dolayı Yıldız Sarayı''nda görevlendirilen Diyarbekirli Sabuncuzade John Louis Alberi tarafından kaleme alınıp, 1898''den sonra II. Abdülhamid''e takdim edilmiştir. ', 'KİTABEVİ', NULL, 7, '1997-12-31', 'full/4f1bec17dd9d5af67b0895af194ec7285f4ee088.jpg', 'TÜRKÇE', ' 6,00', 7),
(8, 'Hac Yolunda', 'Zemin, yeşil rengin garip bir imtizac, derecatıyla adeta yeşil nakışlı, yeşil zeminli bir İran kraliçesi halini almıştı. Hava taze ekinlerin taze rayihasıyla meşbu. Ufuklar kebud ve hande-nak idi. Burada ekiyorlar, şurada biçiyorlar; ötede harman. Zira bu arz-ı hayret bir izdivaç ile birleşiyorlar. Şita, alam-ı bürudet, burada külliyen meçhul. Geniş bir su üzerinden geçtik. ', 'KİTABEVİ', NULL, 8, '1996-12-31', 'full/68feb507e0bbcebdbdfb57b930b53c7bec4df749.jpg', 'TÜRKÇE', ' 10,00', 8),
(9, 'Bir Dumanlı Geçmiş Ki', 'Gömülmesi tohumun toprağa', 'KİTABEVİ', NULL, 9, '1995-12-31', 'full/3f2ca840f7af6a4da0b283be611e3dff746fac87.jpg', 'TÜRKÇE', ' 5,00', 1),
(10, 'Mesafesi Kadar İnleyen Rüzgar', 'Ve olan oldu. O yoğunluktan önce "Yaprak Dili" sızdı. Dünya. Gün. İlk olarak. Ve "Göç". Herkese ne de çok yakışıyor. O çıkılan yamaçlar ve dağ çağrılarına doğru genişledi. Sonra kendisine döndü. Kanamanın mı demeli, yoksa önlenemez bir gerilimin olanca ağırlığı ile "dil"lenmesi mi demeli. "Aşk" vurdu suya. Sonrası. Hep "dua" olarak, dönüp dolaşıp bir "dua" olarak diz çöktü. Ama ateşini kollayarak, "İbrahim" de dört kuş göndermişti dağa. Eskiler "anasır-ı erbaa"yı önemserdi.', 'KİTABEVİ', NULL, 1, '1997-12-31', 'full/04f83ead425c8c2369047f6e7aaba52f437432f5.jpg', 'TÜRKÇE', ' 5,00', 1),
(11, 'Hikmetler Kitabı', 'İşin başında Allah''a yönelmek,', 'KİTABEVİ', NULL, 10, '2008-01-01', 'full/8b26f6aa26420f0e417ee41a0239a6543df18692.jpg', '4. Baskı', ' 10,00', 9),
(12, 'Mahmut Kaptan''ın Anıları', 'Elimizde bulunan eser, "Makale-I Zindancı Mahmud Kapudan Bera-yı Feth ü Zafer - Keşt-I  Maltiz-I Lain-I Duzah-Mekin" adıyla anılmaktadır.', 'KİTABEVİ', NULL, 11, '1996-12-31', 'full/e8c3272c82ea65d8dd39650293d3f38aa0fed423.jpg', 'TÜRKÇE', ' 10,00', 10),
(13, 'Malta Esirleri', 'Bu eser, Üsküdar Selim Ağa Kütüphanesi''nin Kemankeş Emir Hoca bölümündeki 434 numaralı bir mecmuada yer almaktadır. Mecmuanın, yedinci risalesini teşkil eden bu eser, mecmuanın 132a-159a varakları arasında bulunmaktadır. Adi, talik bir hatla istinsah edilen eserin her sayfası 13 satırdan ibarettir. Sayfaların ebadı 154x10-112x78mm. Boyundadır. Eser, Malta''da cereyan eden olaydan üç yıl sonra, Hicri 1010 yılının Şevval (1602 yılının Nisan''ı) ayında Ömer adlı bir katip tarafından istinsah edilmiştir. Şu ana kadar, eserin başka bir nüshasına rastlanmamıştır. Eser, düzgün bir imla ile yazılmıştır. Bu da, Macuncuzade''nin ve Katip Ömer''in klasik bir medrese eğitiminden geçtiklerini göstermektedir.', 'KİTABEVİ', NULL, 12, '1996-12-31', 'full/451c26756f2a05537c4b927d00b660d5a17140f2.jpg', 'TÜRKÇE', ' 10,00', 10),
(14, 'Nabi Ekolü (Divan Edebiyatında)', 'Edebiyat Tarihçilerinin hikemiyat diye adlandırdıkları tefekkür ve hikmete yönelik şiirin özelliğini, genellikle öğüt ve nasihat ifade eden ayet ve hadisler, sosyal ve siyasi olayların hakimane (bilgece) bir biçimde formüle edilmesi, halkın dilinde söylene söylene klişeleşmiş ve onun hayat anlayışını yansıtan atasözleri ve deyimler, kıssadan alınan hisselere düstur olan kelam-ı kibarlar, insanların hafızasında adeta canlı bir tablo gibi yaşayan ve tecessüm eden adet ve geleneklerin gerçek hayata yansıması, ahlaki ve tasavvufi birtakım kavramların öğüt verici tarzda işlenerek insanlar arasında yüzyıllardır oluşturduğu anlayış birliği gibi konular teşkil eder. Hikemi tarz şairleri bu genel çerçeve doğrultusundaki görüşlerini zaman zaman artan zaman zaman da azalan yoğunlukta işlemiş, divanlarında bize yüzyıllardır okunagelen kalıcı şiirler bırakmışlardır. Şairlerimiz, bütün bu ve buna benzer konular dair gözlemlerini, asırların süzgecinden geçerek bu günlere gelmiş zengin ve parlak şiirlerinde vurgulu ve çarpıcı bir şekilde dile getirirler. Bunu ifade ederken, onların asıl üzerinde durduğu ve sanat anlayışlarının özünü oluşturan konu, hikmet ve hakikatı içinde barındıran mananın bir üst perdeden seslendirilmesidir. ', 'KİTABEVİ', NULL, 13, '1996-12-31', 'full/16928bbba111468d9c3b760bb39e25871563bad0.jpg', 'TÜRKÇE', ' 20,00', 11),
(15, 'Hadis''te Metin Tenkidi Metodları', 'Metin tenkidi isnada bir miktar göz kapamayı gerektirir. Metin tenkit edilir ve hadis isnad ölçülerine göre ister sahih, isterse zayıf olsun hakkında uygun olan hüküm  verilir. Metin tenkidi hususunda doğru olan ölçüleri tespit etmek için, Hz. Peygamber''in vefatından sonra sahabenin O''na isnad edilen hadislerle ilgili tutumlarına başvurmak gerekir. Zira onların hadisi kabul etme veya anlama konusunda bir takım kaide ve belli kuralları vardı. Bizler sahabenin hepsinin adalet sahibi, Resulullah''a isnad ettikleri rivayetleri kabul edilen kimseler olduklarını bildiğimize ve bunun da hata etmeleri ve yanlış anlamaları kabul olunsa bile, Hz. Peygamber''den bir başkasına rivayet ettikleri hadislerde doğru oldukları, rivayetlerinde şaibeli ve yalancı olmadıkları anlamına geldiğini bilmekteyiz. Bu durumda sahabe döneminde hadisin iki yönünden birini oluşturan isnat sahih olduğu için geriye metni kalmaktadır. İşte bu konuda sahabenin bazı yaklaşımları, ravinin doğru ve güvenilir olduğunu kabul etmelerine rağmen hadisi reddetme ve geri çevirme veya uygun bir manaya yöneltme hususunda bir takım görüş ve ölçüleri olduğunu biliyoruz. Nitekim onlar zaman zaman bir hadisin ravisini hata veya unutma yahut da yanlış işitme ile itham ediyor, hadisi ya Kur''an''la veya daha çok güvendikleri ve emin oldukları başka sahabenin rivayetiyle çeliştiği için reddediyorlardı. ', 'KİTABEVİ', NULL, 14, '1997-12-31', 'full/033a5d35de985d67ff22cf17acb68a6f62ae50e1.jpg', 'TÜRKÇE', ' 30,00', 12),
(16, 'İslami Türk Edebiyatı', 'İslami Türk edebiyatı kavramı, ilk yazılı ve sözlü mahsullerinden başlayarak günümüze kadar geniş bir edebiyat çalışmaları alanını içine almaktadır. Gerek dini-tasavvufi edebiyat çevresinde gelişen ve gerekse divan edebiyatı mektebine mensup olmakla beraber dini muhteva taşıyan eserler, İslami Türk edebiyatı kavramı içine dahil olmaktadır. Konunun ağırlığını aruz veya hece vezninde yazılmış şiirler teşkil etmekle beraber, nesir halinde günümüze kadar hayatiyetini sürdürmüş edebi mahsuller de hayli bir yekuna ulaşmaktadır.', 'KİTABEVİ', NULL, 15, '2002-12-01', 'full/6d26597f69666cc7be1d51aed4d827610b1be38b.jpg', 'TÜRKÇE', ' 40,00', 11),
(17, 'Çin-Türkistan Hatıraları', 'İdealler, aydınların artık ya bir gençlik heyecanı olarak belleklerini süslüyor ya da bir aksesuar gibi, iğreti biçimde yer alıyor hayatımızda. Oysa, yakın zamanlara kadar, idealler, entelektüel hayatın başlıca belirleyicisiydi. Aydınlar idealleriyle vardı. İdealleri için yaşar, ideallerinin gereklerinin belirlediği bir çizgi üstünde sürdürürlerdi yaşantılarını. İdealleri için savaşan, bütün varlığını, bütün hayatını idealleri uğruna harcayan aydınlar, masal kahramanları kadar uzaklarda kaldı. Rodoslu Habibzade Ahmed Kemal, bu soyu tükenmiş idealist aydınlardan biri. Türkçü, Turancı düşünceleri benimsemiş bir öğretmen, bir İttihatçı. Özel ayrıntılarını bilemediğimiz hayatını, idealleri uğrunda Doğu Türkistan''a yaptığı inanılmaz yolculuk ve orada sürdürdüğü çalışmalar anlamlandırarak günümüze taşıyor. ', 'KİTABEVİ', NULL, 16, '1996-12-31', 'full/b3fe9f9a7cc33df870aa406e0e5ff8d73c46df1e.jpg', 'TÜRKÇE', ' 15,00', 10),
(18, 'Arapça''da Sayılar', 'Arapça''da öğrenilmesi en zor konulardan birisi de, sayıların kuruluşu ve kullanımı konusudur. Bunun sebebi, gerek sayıların kendileriyle, gerekse sayı-sayılan ilişkileriyle ilgili ayrıntılı kuralların ve hemen her kuralın bir istisnasının bulunmasıdır. Sayıların kuruluşunda ve kullanımında, hem Arap asıllı öğrenciler ve hatta yazarlar, hem de özellikle yabancılar zorlanmakta ve çoğu zaman yanlışlık yapmaktadır. Bu durumu düzeltmenin en önemli yolu, sayıların kurallarını iyi kavramak ve bol bol örnekler kurarak cümle içinde kullanmaktır.', 'KİTABEVİ', NULL, 17, '1998-12-31', 'full/91a022e14ed41ae26f001fd6faf004d004b429ac.jpg', 'TÜRKÇE', ' 25,00', 13),
(19, 'Külliyat-ı Letaif', 'Malumdur ki latifeler hoşa gidecek ve ekseriya gülünecek sözlerle bu yolda nakledilen gerçek-yalan fıkralardır. Ama bunların içinde hikmet düsturu sayılacak pek çok yüksek söz, ibret dersi alınacak kıssa vardır. Öteden beri her kavmin edebiyatçı ve hikmet sahibi kimseleri ciddi bir dille anlatılamayacak hikmet ve öğütleri, hatta bazan hayvan ve bitkilerin lisanından hezel ve mizah vadisinde fıkralar ihtiraıyla halka telkine, bu yolla ahlakı tezhibe ve gafilleri tenbihe himmet etmişlerdir. Binaenaleyh latifeler hem gönülleri şenlendirme vesilesi olması, hem de ibret almayı, hakikatleri öğrenmeyi sağlaması yönüyle eğlencelerin faydalı kısmındandır. ', 'KİTABEVİ', NULL, 18, '1998-12-31', 'full/dca20d914e909ae1d826668466818cf9371bcecb.jpg', 'TÜRKÇE', ' 45,00', 14),
(20, 'İslam''da Felsefe Akımları', 'İzmirli''nin önemi, yalnız eserlerinin değerinden ileri gelmez; kimi bilim dallarında "kurucu" bir görev yapmasından da kaynaklanır. Söz gelimi İzmirli, Türkiye''de felsefe tarihinin gerçek anlamda kurucusudur. Kendisinden önce bu alanda yapılmış bir çalışma yoktur ve bu çalışmaları, sonraki tüm çalışmalara öncülük, kaynaklık etmiştir. Ama ne yazık ki, O''nun bu alandaki çalışmaları da, tıpkı Kelam alanındaki özgün çalışmaları gibi, sürdürülüp geliştirilememiştir.', 'KİTABEVİ', NULL, 19, '1997-12-31', 'full/fd16ee24d1310fefe28d8054049ab040d23e8a61.jpg', 'TÜRKÇE', ' 25,00', 15),
(21, 'İstanbul''da Yarım Asırlık Vekayi-i Mühimme', 'Hatıralar, tarihi hadiseleri olduğu gibi nakleden eserler değildir ancak Türk siyasi tarihini muhakeme ederken hadiselerin mümkün olduğu kadar vuzuha kavuşabilmesi, büyük ölçüde onların şehadetine bağlıdır. Bununla beraber Türk edebiyatı açısından da vazgeçilmez birer kaynak olan hatıralar, edebiyatçıların yetiştiği ortamların siyasi, sosyal ve kültürel zeminini bütün cepheleriyle tesbit edebilmek ve onların meydana getirdiği edebi eserlere ve akımlara tam manasıyla vakıf olabilmek bakımından da büyük bir öneme sahiptir. Bu düşünceden hareketle, 1866''dan başlayarak 1908''e kadar devam eden yaklaşık yarım asırlık bir devreye, zaman zaman hissi de olsa ışık tutan ve XIX. Asrın ikinci yarısında devrin en tesirli yayın organı olan Basiret gazetesinin sahibi Ali Efendi''nin kaleme aldığı bu eseri, üzerinde biraz çalışarak hem orijinal dili ve üslubuyla hem de sadeleştirerek günümüz okuyucusuna kazandırmaya çalıştık. ', 'KİTABEVİ', NULL, 20, '1997-12-31', 'full/0b257ea025fb399ab53e62fdc9b60ee696d6c3ac.jpg', 'TÜRKÇE', ' 20,00', 10),
(22, 'İsviçre''de Jöntürk Basını Ve Türk Siyasal Hayatına Etkileri (1889-1902)', 'Osmanlı İmparatorluğu''nda siyasal baskıdan bunalıp, Avrupa''ya firar edenlerin kendilerini ve ülkelerinin dertlerini ifade edebilecekleri tek yol basın olmuştur. Önceleri samimi olarak fikirlerini yaymak için tuttukları neşir yolundan, sonra bazı kimseler çeşitli vaatlerle ayrılmak zorunda kalmıştır. Neşriyatı bırakmayanlar çok azdır. Ama bu yayın faaliyetinin önemli görevler de ifa ettiği su götürmez bir gerçektir. ', 'KİTABEVİ', NULL, 21, '1995-12-31', 'full/d17d43cb8dd02e2cdd555f35b6e6078d3e46b263.jpg', 'TÜRKÇE', ' 10,00', 3),
(23, 'İstanbul''dan Ben de Geçtim', '"Her insan ömrünü olduğu gibi bilhassa Selim Nüzhet Gerçek''in ömrünü terkip eden unsurlar, birer birer ele alınıp tahlil edilince görülür ki bunlar kalıpları itibariyle maddi meşgaleler, meşakkatler, gayretler, zahmetler şeklinde tezahür etmekle beraber, hüviyetleri itibariyle manevi şeylerdir; kanımızla canımızla beslediğimiz ümitler, hülyalar, istekler, sevgiler, kuruntulardan ibarettir. Zaten bu yüzden sürüklediğimiz maddiyat aleminin zincirleri hep birer maneviyat aleminin halkalarına döner."', 'KİTABEVİ', NULL, 22, '1997-12-31', 'full/24919b38cc4ac9d342903bdd29694afa8e3f872b.jpg', 'TÜRKÇE', ' 25,00', 10),
(24, 'Tanzimat Sonrası Türk Edebiyatında Yeni Fikirler', 'Bu çalışmada, Yeni edebiyatın oluşması ve gelişmesi açısından farklı bir boyuta dikkat çekilmektedir: Tanpınar''ın, modern Türk edebiyatının bir medeniyet krizi ile başladığına dair hayati tespiti, yakın zamanlara kadar pek bilinmeyen, günümüzdeyse akıbeti meçhul bir cereyan ile anlatılmıştır. Yeni prensipler, yeni insan, yeni hayat, yeni medeniyet, yeni düzen ve yeni surette var olacak yeni bir edebiyat ve yine terakki. Bu medeniyet ve terakki, yeni Türk edebiyatının "hüsn ü aşk"ıdır. Bunun en açık ve anlamlı göstergelerinden biri, mevcut kateşizm metinleridir. ', 'KİTABEVİ', NULL, 23, '1998-12-31', 'full/2a8087eed75d9fd3efc07a8081be38f589802404.jpg', 'TÜRKÇE', ' 35,00', 11),
(25, 'Kur''an''da Siyasi Kavramlar 3.hamur', 'Kur''an-ı Kerim''in daha iyi anlaşılabilmesi için pek çok yönden incelemeler yapılmış ve yapılmaktadır. Elinizdeki çalışma, kavramcı yöntemle, Kur''an''ın belli bir kavram öbeği çerçevesinde yapılmış, değişik bir çalışmadır. İslam kamu hukuku ve siyasi literatüründe ya da siyasi söylemlerde yer alan doğrudan siyasi içerikli veya zaman içinde siyasi anlam yüklenen Kur''an kavramları; köken, türev, anlam, bağlam ve bütünlük çerçevesinde ele alınmış, kavramın özelliğine göre sistemleştirilerek incelenmiştir.', 'KİTABEVİ', NULL, 17, '1998-12-31', 'full/bcc0ce800734fc460b3f55b1ea1944bfd05583a7.jpg', 'TÜRKÇE', ' 18,00', 16),
(26, 'Servet-i Fünun''a Kadar Yeni Türk Şiirinde Şekil Değişmeleri', 'Türk Edebiyatı 19. Asırda gelişme seyri ve gösterdiği değişiklikler yönünden hem nazım hem de nesir sahalarında bir dönüm noktasına gelmiştir. Edebiyatımızda köklü değişmelerin vuku bulduğu bu devrede şiirimizin biçim yönünden ne gibi değişiklikler ve dalgalanmalar gösterdiğinin detaylı bir şekilde araştırılması, Türk şiir tarihinin bir cephesini aydınlatacaktır.', 'KİTABEVİ', NULL, 24, '1997-12-31', 'full/144677cde819757d15f26c918cce91088c1346d5.jpg', 'TÜRKÇE', ' 20,00', 11),
(27, 'Medeniyet ve Modernlik', 'Medeniyet, modernlik, kapitalizm, globalizm. Aynı hikayenin farklı kalıplarla ifadesi. Hikaye: Avrupalı beyaz adamın dünyayı, hayır kainatı fethi. Sonuç? Nihilizm, hiçperestlik. Kainatı fetheden modern insan, kendi kalbini fethedemedi. Keşfedemedi bile.', 'KİTABEVİ', NULL, 25, '1997-12-31', 'full/43704b61755069e2d5839ca75be1fcc86543b7c4.jpg', 'TÜRKÇE', ' 8,00', 17),
(28, 'Ticaret Savaşları', 'Soğuk Savaşın sonu ve iki Almanya''nın (iki Avrupa''nın!) birleşmesiyle Amerikan hegemonyası bitti; yeni bir güçler dengesi dönemine girdik. Üç iktisadi blok ortaya çıktı: Kuzey Amerika, Batı Avrupa ve Doğu Asya. ABD ekonomik pençeleri sökülen siyasi askeri dev; diğerleri siyasi askeri pençeleri henüz oluşmamış ekonomik devler. Yirmibirinci yüzyıl bu üç blok arasındaki kıran kırana ticaret savaşlarına sahne olacak. Kapitalist sistemin geçmişindeki bütün ticaret savaşları askeri savaşlarla sonuçlandı ve nihai savaşı kazanan taraf "Hegemonik güç" oldu. Ondokuzuncu yüzyılda İngiltere, Yirminci yüzyılda ABD. Eski hegemonik güç, kazanan tarafta yer aldı, fakat ikinci derecedeki aktör olarak! Bu sefer, Batı-dışı yeni ekonomik ve askeri güçlerin (Japonya, Çin) devreye girmesiyle, çatışma ve ittifaklar daha kaotik bir ihtiva kazanıyor. Mustafa Özel kısa ve özlü yazılarıyla bu ekonomik rekabetin ekonomi dışı yönlerine ışık tutuyor. Jeopolitik ve jeokültürel içerimlerini tartışıyor. ', 'KİTABEVİ', NULL, 25, '1997-12-31', 'full/a2afb06d0e18a569d217c38876ecc8e80dc10333.jpg', 'TÜRKÇE', ' 8,00', 18),
(29, 'Birey Burjuva Ve Zengin', 'Batı birey ve burjuvanın vatanı. Doğu cemaat ve zenginin. Batı burjuvazisi cemaat halinde sindiremediği zümreleri "bireyleştirerek" ehlileştirdi, kapitalizm için zararsız "yurttaşlar" haline getirdi. Türedi Doğu Burjuvazisi ne bireyleşebildi ne cemaat dinamizminden yararlanabildi. Japonlar, sancılı bir bireyleşmeyi cemaat yapısıyla uzlaştırma arayışına girdiler. Böylece hem bireyleşme ve burjuvalaşma gerçekleşti hem cemaatleşme dönüşerek devam etti. Modern Japonya''da her şirket bir cemaattir. Büyük ortaklarla tepe yöneticileri bireyleşip burjuvalaşanlar, diğerleri bireyleştirilip itaatkar yurttaş kılınanlar. Diğer bütün toplumlarda sonuçsuz bir temrin oldu bu süreç. Batı''ya uyanlar da "kaybetti", uymayanlar da. Belki hem uymak, hem uymamak gerekiyor. Ama nasıl? ', 'KİTABEVİ', NULL, 25, '1998-12-31', 'full/ed25f83554ad498973ac19660c5d7dcb766c2558.jpg', 'TÜRKÇE', ' 10,00', 19),
(30, 'Sultan Ahmed Divanı''nın Tahlili', 'Genç yaşta saltanat koltuğuna oturduktan sonra, adını Sultan Ahmed Camii gibi bir şaheserle ölümsüzleştiren Osmanlı Sultanını ölümsüz kılmaya yetecek bir başka eser. Dr. İsa Kayaalp''in bu değerli çalışması, Sultan Ahmed''in dönemi, hayatı ve sanatı hakkında verdiği bilgiler, yaptığı oylumlu tahlillerle söz''ün kudreti ile kudretin söz''ü üzerine kurulmuş bir dünyayı aydınlatıyor. Ve ilk kez Sultan Ahmed Divanı, arandığı zaman her şeyin bir arada bulunabileceği bir bütün haline geliyor. ', 'KİTABEVİ', NULL, 26, '1999-12-31', 'full/8a1e6dba9aab77530aeeb1cbbf77427627c187e1.jpg', 'TÜRKÇE', ' 25,00', 11),
(31, 'Türkçe Kur''an ve Cumhuriyet İdeolojisi', 'Mustafa Kemal Atatürk''ün son dileği, ezandan başka ibadetleri de Türkçe olarak yaptırmak ve Türk kafasını Arap kafası köleliğinden kurtarmaktı. Türk Ocağı''na gittiğimiz gün, Kur''an''ı Türkçe''ye çevirmek konusunu açmıştı. Orada bulunan Kazım Karabekir şöyle dedi:', 'KİTABEVİ', NULL, 27, '1998-03-01', 'full/7b45816f61465fc671f6db570378bab36884e579.jpg', 'TÜRKÇE', ' 30,00', 20),
(32, 'Afganistan ve Türkler', 'Nüfusunun mühim bir kısmını Türklerin teşkil ettiği Afganistan ile Türkiye arasında tarih ve kültür bakımından pek çok müşterek taraflar bulunmasına rağmen, maalesef, memleketimizde Afganistan hakkında ciddi bir araştırma yapılmamıştır. Bu eksikliği gidermek ümidiyle "Dünden Bugüne Afganistan" adı altındaki araştırmamızı bir müddet evvel neşretmiştik. Bu araştırma esnasında müşahade ettiğimiz pek çok önemli husustan biri de Türkiye''nin, yarım asra yakın bir devre Afganistan''ın kaderine müsbet bir şekilde nasıl tesir ettiği idi. Bu hususu, Afgan tarihinin akışı içinde teferruatlı olarak işlemek mümkün olmadığından, ayrı bir çalışma mevzuu yapmak istedik. Bu vesileyle hem Türk-Afgan münasebetlerinin tarihçesini vermek hem de yirminci asrın ilk yarısında Türklerin, Afganistan''ın kaderine nasıl tesir ettiğini etraflıca tartışmak istedik. ', 'KİTABEVİ', NULL, 28, '1997-02-25', 'full/ba5c48c1ce7cfcf9d5c530cc00419cd2f2343894.jpg', 'TÜRKÇE', ' 15,00', 21),
(33, 'Amel Defteri', 'Mısır valisi Kavalalı Mehmet Ali Paşa Machiavelli''nin Hükümdar''ını peyderpey tercüme etmekle görevlendirdiği Artin''e, ilk üç dosyayı okuduktan sonra şunları söylüyor: "Machiavelli''den çevirdiğin bütün parçaları okudum. İlk on sayfada yeni olan pek fazla bir şey görmedim, fakat gelişeceğini ümit ediyordum. Bir sonraki on sayfa da daha iyi değildi. Sonuncusu ise bütünüyle alelade. Machiavelli''den öğrenecek fazla bir şeyimin olmadığını görüyorum; hileye dair onun bildiklerinden daha fazla malumat sahibiyim. Onu tercüme etmene artık gerek yok." ', 'KİTABEVİ', NULL, 29, '1998-12-31', 'full/03b753d9e095891a6324dd7b28798d5d4f35269e.jpg', 'TÜRKÇE', ' 10,00', 20),
(34, 'Edebi Hatıralar', 'Mehmet Rauf''un farklı yayın organlarında çıkan ve büyük kısmı "edebi hatıralar" üst başlığıyla yayınlanan yazıları bu kitabı şekillendirdi. Biz de onun seçtiği bu ismi, benzer isimde başka kitaplar olsa da aynen muhafaza ettik. Bunun dışında yine farklı dergilerde çıkmış ve onun hayatına ışık tutan hatıra niteliğindeki bazı yazı ve anketlere, bir de mülakata yer verdik. İfadede ve imlada bugün için yanlış görülen kullanımlar olsa da değiştirme yoluna gitmeden aynen almayı tercih ettik. Hatıraların sıralanmasında ise yayın tarihlerini değil, bir bütünlük teşkil etmesi amacıyla, anlatılan hadiselerin kendi içindeki kronolojik sırasını takip ettik.', 'KİTABEVİ', NULL, 30, '2008-05-07', 'full/1bad72fc41eb98bca1cb8edf11f38447e0afd4b2.jpg', '2. Baskı', ' 8,00', 8),
(35, 'Çocuk Gibi Olun', '"Çocuk gibi olun" Tolstoy''un Türkçe''ye çevrilmiş otuza yakın filozofik denemelerinin bir bölümünü oluşturmaktadır. 1920-1923 yılları arasında Ali Fuad tarafından "Rus Meşahir-I Hukemasından Tolstoy''un Asarından" başlığıyla çevrilen ve eski harflerle basılan eser, bu kez Akın Yeşilbaş''ın sadeleştirmesiyle günümüz okuru için yeniden hazırlanmıştır.', 'AYIŞIĞI KİTAPLARI', NULL, 31, '1998-12-31', 'full/1623274541a1c18670f3ef484df2c7b69fef9b5f.jpg', 'TÜRKÇE', ' 8,00', 22),
(36, 'Gülistanbul', 'Fethini müjdelemiş cihana gül, İstanbul', 'AYIŞIĞI KİTAPLARI', NULL, 32, '1999-12-31', 'full/a71275d3510ce0ca7039dcf978f5332fcc00809c.jpg', 'TÜRKÇE', ' 8,00', 1),
(37, 'Kutsalın Dönüşü', '"Kutsalın Dönüşü ve Yeni Toplum Arayışları" bir "uygulamalı din sosyolojisi" çalışmasıdır. Bu araştırma sürecinde toplanan veriler, modernizmin dışladığı din olgusunun, modernizm sonrası toplum yapılanmasında, insanlığın gündemine yeniden girdiğini göstermiştir.', 'AYIŞIĞI KİTAPLARI', NULL, 33, '1998-12-31', 'full/1179f1d28e5a3c32cf8f0885431993c5ed7737d1.jpg', 'TÜRKÇE', ' 5,00', 23),
(38, 'Segah Peşrev Bir İttihat Ve Terakki Romanı', 'Haziran 1909 ', 'AYIŞIĞI KİTAPLARI', NULL, 34, '1999-12-31', 'full/c33d4543be47a110767ef2247eee5e8cc2d0236e.jpg', 'TÜRKÇE', ' 10,00', 24),
(39, 'İnsanoğlu Nereye Gidiyorsun', 'Eserlerini büyük ölçüde "pozitivist" mantıkla kaleme alan ünlü Hırvat yazar Filip Çorlukiç''in, "efsaneler ve yaşayan dinlerin felsefi yönlerini" sözkonusu ettiği bilim kurgu türünde yazılmış, insanoğlunun yeryüzündeki serüvenini sorgulayan bu eseri, yayınlandığı günlerde pek çok çevirisi ile Batıda büyük yankılar uyandırmıştır.', 'AYIŞIĞI KİTAPLARI', NULL, 35, '1998-12-31', 'full/6a2324c5eea4603de61be35070a1442bef162d3f.jpg', 'TÜRKÇE', ' 20,00', 23),
(40, 'Hikayelerim (kod1)', '"En dakik Şeriat mihengi" ne vurulduktan sonra bütünleştirilen ve bütünleştirilecek olan eserleri üzerinde bu ölçüyü devam ettirmeye başlar ve en titiz murakabeyi sürdürmek borcunu üstlenirken; ', 'BÜYÜK DOĞU YAYINLARI', NULL, 36, '2012-07-01', 'full/3a57b5c9444a531288cf13991af046952dd20c76.jpg', '20. Baskı', ' 18,00', 25),
